package com.tnsif.Day2;

public class ForEachLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i[] = {5,10,15,20,25};
		for(int x:i) {
			System.out.println(x);
		}
		
		char ch[]= {'a','i','u'};
		for(char x:ch) {
			System.out.println(x);
		}
		
		String s[] = {"Akshay","Dia"};
		for(String x:s) {
			System.out.println(x);
		}
		

	}

}
